mapper.select:
select a.id,a.create_time,a.name,a.logo,a.creator,a.creator as 'creatorMember.id',a.managers,a.tags,a.introduce, a.can_post,a.status,a.topic_review,(select count(c.id) from tbl_group_topic as c where group_id=a.id and c.status=1) as topicCount, (select count(*) from tbl_group_fans as d where group_id=a.id) as fansCount, b.name as 'creatorMember.name' from tbl_group as a left join tbl_member as b on a.creator=b.id
and a.name LIKE #{key}
and a.status=#{status}
order by a.id DESC

mapper.update:
update tbl_group
name = #{name}, logo = #{logo}, tags = #{tags}, managers = #{managers}, introduce = #{introduce}, can_post = #{canPost}, topic_review = #{topicReview},
where id = #{id}
