mapper.select:
select a.id,a.id as weibo_id,a.create_time,a.member_id as 'member.id',b.name as 'member.name',b.avatar as 'member.avatar', a.type,a.content,a.favor,a.status,(select count(id) from tbl_weibo_comment as d where d.weibo_id=a.id) commentCount, (CASE WHEN (SELECT id FROM tbl_weibo_favor WHERE weibo_id=a.id AND member_id=#{loginMemberId}) IS NULL THEN 0 ELSE 1 END) isFavor from tbl_weibo a left join tbl_member b on a.member_id = b.id
and a.content LIKE #{key}
a.member_id=#{memberId}
order by a.id DESC

mapper.delete:
delete from tbl_weibo_favor where weibo_id = #{id};
delete from tbl_weibo_comment where weibo_id = #{id};
delete from tbl_action_log where type=2 and foreign_id = #{id};
delete from tbl_weibo where id = #{id}
